import React from 'react';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';
import RoomDetails from './RoomDetails';
import DatePicker from './DatePicker';
import GuestInfo from './GuestInfo';

const BookingForm = () => {
  const validationSchema = Yup.object().shape({
    fullName: Yup.string().required('Full Name is required'),
    email: Yup.string().email('Invalid email').required('Email is required'),
    roomType: Yup.string().required('Please select a room type'),
    checkInDate: Yup.date().required('Check-in date is required'),
    checkOutDate: Yup.date().required('Check-out date is required'),
    guests: Yup.number().min(1, 'At least 1 guest').required('Guest count is required'),
  });

  const { register, handleSubmit, formState: { errors } } = useForm({
    resolver: yupResolver(validationSchema),
  });

  const onSubmit = (data) => {
    console.log(data);
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="booking-form">
      <RoomDetails register={register} errors={errors} />
      <DatePicker register={register} errors={errors} />
      <GuestInfo register={register} errors={errors} />
      <div className="form-group">
        <label>Full Name</label>
        <input {...register('fullName')} type="text" />
        <p>{errors.fullName?.message}</p>
      </div>
      <div className="form-group">
        <label>Email</label>
        <input {...register('email')} type="email" />
        <p>{errors.email?.message}</p>
      </div>
      <button type="submit" className="btn-book-now">Book Now</button>
    </form>
  );
};

export default BookingForm;
