import React, { useState } from 'react';
import DatePicker from './DatePicker';
import GuestInfo from './GuestInfo';
import RoomDetails from './RoomDetails';

const BookingForm = () => {
    const validationSchema = Yup.object().shape({
        fullName: Yup.string().required('Full Name is required'),
        email: Yup.string().email('Invalid email').required('Email is required'),
        roomType: Yup.string().required('Please select a room type'),
        checkInDate: Yup.date().required('Check-in date is required'),
        checkOutDate: Yup.date().required('Check-out date is required'),
        guests: Yup.number().min(1, 'At least 1 guest').required('Guest count is required'),
      });
  const [formData, setFormData] = useState({
    roomType: '',
    checkInDate: '',
    checkOutDate: '',
    guests: 1,
    fullName: '',
    email: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="booking-form">
      <RoomDetails handleInputChange={handleInputChange} />
      <DatePicker handleInputChange={handleInputChange} />
      <GuestInfo handleInputChange={handleInputChange} />
      <div className="form-group">
        <label>Full Name</label>
        <input
          type="text"
          name="fullName"
          value={formData.fullName}
          onChange={handleInputChange}
          required
        />
      </div>
      <div className="form-group">
        <label>Email</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleInputChange}
          required
        />
      </div>
      <button type="submit" className="btn-book-now">Book Now</button>
    </form>
  );
};

export default BookingForm;
