import React, { useState } from 'react';
import axios from 'axios';

const BookingForm = () => {
  const [formData, setFormData] = useState({
    roomType: '',
    checkInDate: '',
    checkOutDate: '',
    guests: 1,
    fullName: '',
    email: ''
  });

  const [successMessage, setSuccessMessage] = useState('');

  // Make this function async
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Ensure you are inside an async function when using await
      const response = await axios.post('http://localhost:8000/api/book', formData);
      setSuccessMessage('Booking successful!');
      console.log(response.data);
    } catch (error) {
      console.error('There was an error making the booking', error);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input 
        type="text" 
        name="fullName" 
        value={formData.fullName} 
        onChange={handleChange} 
        placeholder="Full Name" 
      />
      <input 
        type="email" 
        name="email" 
        value={formData.email} 
        onChange={handleChange} 
        placeholder="Email" 
      />
      {/* Add other form fields like roomType, checkInDate, etc. */}
      <button type="submit">Book Now</button>
      {successMessage && <p>{successMessage}</p>}
    </form>
  );
};

export default BookingForm;
